mapper.select:
select t.*, `od3`.`name` AS `regionName`, `od2`.`name` AS `branchName`, `od`.`name` AS `departmentName`, CASE WHEN t.user_attribute = '1' THEN `od6`.`name` ELSE `od3`.`name` END `user_region_name`, CASE WHEN t.user_attribute = '1' THEN `od5`.`name` ELSE `od2`.`name` END `user_branch_name`, CASE WHEN t.user_attribute = '1' THEN `od4`.`name` ELSE `od`.`name` END `user_department_name`, `od6`.`name` AS `referrer_region_name`, `od5`.`name` AS `referrer_branch_name`, `od4`.`name` AS `referrer_department_name` from (SELECT a.user_id, u.username, ui2.truename AS referrer_true_name, u.mobile, CASE ui.role_id WHEN 1 THEN '出借人' WHEN 2 THEN '借款人' END AS roleid , ui.attribute AS user_attribute, ui.truename, spu.username AS referrer_name, a.total, a.balance, a.frost, a.await, a.repay, a.rec_money, a.fee, a.in_money, a.balance_cash, a.balance_frost, a.id, CASE WHEN ui.attribute = 2 OR ui.attribute = 3 THEN a.user_id ELSE su.spreads_userid END AS hyd_id, su.spreads_userid, IFNULL(a.plan_balance,0) as plan_balance, IFNULL(a.plan_frost,0) as plan_frost, IFNULL(a.balance,0) + IFNULL(a.plan_balance,0) as balance_total, IFNULL(a.frost,0) + IFNULL(a.plan_frost,0) as frost_total FROM huiyingdai_account a LEFT JOIN huiyingdai_users u ON a.user_id = u.user_id LEFT JOIN huiyingdai_users_info ui ON a.user_id = ui.user_id LEFT JOIN huiyingdai_spreads_users su ON a.user_id = su.user_id LEFT JOIN huiyingdai_users_info ui2 ON ui2.user_id = su.spreads_userid LEFT JOIN huiyingdai_users spu ON spu.user_id = su.spreads_userid ) t LEFT JOIN `hyjf_crm`.`oa_users` `ou` ON `ou`.hyd_id = t.hyd_id AND ou.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN `hyjf_crm`.`oa_department` `od` ON `od`.`id` = `ou`.`departmentid` AND `od`.id IS NOT NULL LEFT JOIN `hyjf_crm`.`oa_department` `od2` ON `od2`.`id` = `od`.`parentid` LEFT JOIN `hyjf_crm`.`oa_department` `od3` ON `od3`.`id` = `od2`.`parentid` LEFT JOIN `hyjf_crm`.`oa_users` `ou1` ON `ou1`.hyd_id = t.spreads_userid AND ou1.user_status IN ('E','Q1','Q11','Q2','Q21') LEFT JOIN `hyjf_crm`.`oa_department` `od4` ON `od4`.`id` = `ou1`.`departmentid` AND `od4`.id IS NOT NULL LEFT JOIN `hyjf_crm`.`oa_department` `od5` ON `od5`.`id` = `od4`.`parentid` LEFT JOIN `hyjf_crm`.`oa_department` `od6` ON `od6`.`id` = `od5`.`parentid` WHERE 1 = 1
ORDER BY t.user_id DESC
LIMIT #{limitStart} , #{limitEnd}
